import React from 'react';
import PropTypes from 'prop-types';
import {connect} from 'react-redux';
import {compose} from 'recompose';
import {WebexDataProvider, WebexMeeting} from '@webex/components';
import WebexSDKAdapter from '@webex/sdk-component-adapter';

function MyWidget(props) {
  const adapter = new WebexSDKAdapter(props.sparkInstance);

  return (
    <WebexDataProvider adapter={adapter} >
      <WebexMeeting meetingDestination="Y2lzY29zcGFyazovL3VzL1JPT00vZjExNTc4ZjAtY2VjYi0xMWU5LTlhNWYtYzEwNjhhNzNlYThi" />
    </WebexDataProvider>
  );
}

MyWidget.propTypes = {
  sparkInstance: PropTypes.object.isRequired
};

export default compose(
  connect()
)(MyWidget);
